ConfigTemplate.create(name: 'Ethernet Standard', description: 'Ethernet standard, 2 ethernet ports WAN/LAN', config_text: "/interface ethernet\r\nset [ find default-name=ether2 ] name=ether2-master\r\nset [ find default-name=ether3 ] master-port=ether2-master\r\nset [ find default-name=ether4 ] master-port=ether2-master\r\nset [ find default-name=ether5 ] master-port=ether2-master\r\nset [ find default-name=ether6 ] name=ether6-master\r\nset [ find default-name=ether7 ] master-port=ether6-master\r\nset [ find default-name=ether8 ] master-port=ether6-master\r\nset [ find default-name=ether9 ] master-port=ether6-master\r\nset [ find default-name=ether10 ] master-port=ether6-master\r\n/ip neighbor discovery\r\nset ether1 discover=no\r\n/ip dns set allow-remote-requests=yes\r\n/ip firewall filter\r\nadd chain=input comment=\"defconf: accept ICMP\" protocol=icmp\r\nadd chain=input comment=\"defconf: accept establieshed,related\" \\\r\n    connection-state=established,related\r\nadd action=fasttrack-connection chain=forward comment=\"defconf: fasttrack\" \\\r\n    connection-state=established,related\r\nadd chain=forward comment=\"defconf: accept established,related\" \\\r\n    connection-state=established,related\r\nadd action=drop chain=forward comment=\"defconf: drop invalid\" \\\r\n    connection-state=invalid\r\n/tool mac-server\r\nset [ find default=yes ] disabled=yes\r\nadd interface=ether2-master\r\n/tool mac-server mac-winbox\r\nset [ find default=yes ] disabled=yes\r\nadd interface=ether2-master\r\n/ip service disable 0,1,2,3,4,5,7\r\n/ip service set winbox address=5.157.64.37,185.113.80.137,192.168.88.100\r\n/system clock set time-zone-name=Europe/London\r\n/system ntp client set enabled=yes server-dns-names=0.uk.pool.ntp.org\r\n/system package update set channel=bugfix\r\n")